sqlMap.statement:
SELECT ROWNO AS "ROWNO", PrptyGroupName AS "PRPTYGROUPNAME", PrptyGroupDesc AS "PRPTYGROUPDESC" FROM ( SELECT ROW_NUMBER() OVER(ORDER BY PrptyGroupName) ROWNO, PrptyGroupName , PrptyGroupDesc FROM $schemaId$.TSEAIBP01
UPPER(PrptyGroupName) LIKE '%' || #searchPrptyGroupName# || '%'
) ORGTABLE WHERE ROWNO BETWEEN #startNum# AND #endNum#
