sqlMap.statement:
SELECT ROWNO AS "ROWNO", MSG AS "MSG" , MSGCTNT AS "MSGCTNT" , TREATMATRCTNT AS "TREATMATRCTNT" FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MSG) ROWNO, MSG , MSGCTNT , TREATMATRCTNT FROM $schemaId$.TSEAIMS01
UPPER(MSG) LIKE '%' || #searchMsg# || '%'
) ORGTABLE WHERE ROWNO BETWEEN #startNum# AND #endNum#
