SELECT ROWNO, BZWKSVCKEYNAME , EAISVCNAME , (SELECT COLUMNVALUE FROM $schemaId$.TSEAIHS05 HS05 WHERE B.BZWKSVCKEYNAME = HS05.BZWKSVCKEYNAME AND HS05.COLUMNNAME='COMMON_SRVCID' ) COMMON_SRVCID FROM ( SELECT ROWNUM AS ROWNO, A.* FROM ( SELECT HS04.BZWKSVCKEYNAME , HS04.EAISVCNAME FROM $schemaId$.TSEAIHS04 HS04 JOIN $schemaId$.TSEAIHE01 HE01 ON (HE01.EAISVCNAME = HS04.EAISVCNAME ) WHERE EAIBZWKDSTCD IN (select EAIBZWKDSTCD from $schemaId$.TSEAIRM06 where UserId=#loginId#)
UPPER(BZWKSVCKEYNAME) LIKE '%'||#searchBzwkSvcKeyName#||'%'
) A ) B WHERE ROWNO BETWEEN #startNum# AND #endNum#
