sqlMap.statement:
SELECT ROWNO AS "ROWNO", EAISvcName AS "EAISVCNAME", EAIBzwkDstcd AS "EAIBZWKDSTCD", EAITRANNAME AS "EAITRANNAME", EAISvcDesc AS "EAISVCDESC", io AS "IO", SplizDmndDstcd AS "SPLIZDMNDDSTCD", StndTelgmWtinExtnlDstcd AS "STNDTELGMWTINEXTNLDSTCD", FromName AS "FROMNAME", ToName AS "TONAME", DevUser AS "DEVUSER", DevUserName AS "DEVUSERNAME", BankUser AS "BANKUSER", BankUserName AS "BANKUSERNAME", CreateBy AS "CREATEBY", UpdateBy AS "UPDATEBY", CreateByName AS "CREATEBYNAME", UpdateByName AS "UPDATEBYNAME" FROM ( SELECT ROW_NUMBER() OVER(ORDER BY HE01.EAISvcName) ROWNO , HE01.EAISvcName , HE01.EAIBzwkDstcd , SUBSTR( HE01.EAISvcName, 1, LENGTH(HE01.EAISvcName)-2) EAITRANNAME , HE01.EAISvcDesc , CASE WHEN '$schemaId$' = 'EAI' THEN CASE WHEN SUBSTR(HE01.EAISVCNAME, LENGTH(HE01.EAISVCNAME)-1, 2) IN ('S2','R2') THEN 'O' ELSE 'I' END ELSE CASE WHEN SUBSTR(HE01.EAISvcName, LENGTH(HE01.EAISvcName)-1, 2) in ('S2','R1') THEN 'O' ELSE 'I' END END io , SUBSTR(HE01.EAISVCNAME, LENGTH(HE01.EAISVCNAME)-1, 1) SplizDmndDstcd , SUBSTR(HE01.EAISVCNAME, LENGTH(HE01.EAISVCNAME), 1) StndTelgmWtinExtnlDstcd , SUBSTR( HE01.GstatSysAdptrBzwkGroupName,2,3) FromName , SUBSTR( HE02.PsvSysAdptrBzwkGroupName,2,3) ToName , HE01.DevUserId DevUser , '' DevUserName , HE01.BankUserId BankUser , '' BankUserName , HE01.CreateBy , HE01.UpdateBy , '' CreateByName , '' UpdateByName FROM $schemaId$.TSEAIHE01 HE01 LEFT OUTER JOIN $schemaId$.TSEAIHE02 HE02 ON HE01.EAISvcName = HE02.EAISvcName AND HE02.SvcPrcssNo = 1 WHERE HE01.EAIBzwkDstcd IN ( select EAIBZWKDSTCD from $schemaId$.TSEAIRM06 where USERID=#loginId# )
HE01.EAISvcName LIKE '%' || #eaiSvcName# || '%'
) AS TB WHERE ROWNO BETWEEN #startNum# AND #endNum#

INSERT INTO $schemaId$.TSEAITR01 ( 
    CNVSNNAME, CNVSNDESC, LASTAMNDHMS, CREATEBY, UPDATEBY
) VALUES (#cnvsnName#, #cnvsnDesc#, TO_CHAR(LOCALTIMESTAMP,'YYYYMMDDHH24MISS') , #loginId#, #loginId#)

INSERT INTO $schemaId$.TSEAITR03 ( 
    CNVSNNAME, CNVSNRSULTITEMPATHNAME, CNVSNCMDNAME, CNVSNITEMBASCVAL, CNVSNITEMSERNO
) VALUES (#cnvsnName#, #cnvsnRsultItemPathName#, #cnvsnCmdName#, #cnvsnItemBascVal#, CAST(#cnvsnItemSerno# AS NUMERIC))

INSERT INTO $schemaId$.TSEAITR01 ( 
    CNVSNNAME, CNVSNDESC, LASTAMNDHMS, CREATEBY, UPDATEBY
) VALUES (#cnvsnName#, #cnvsnDesc#, TO_CHAR(LOCALTIMESTAMP,'YYYYMMDDHH24MISS'), #loginId#, #loginId#)
